module.exports.getAdminAccess = (body) => {
	try {
		let oneArray = ["verified-artist", "delete-artWork"];
		let secondArray = [
			...oneArray,
			"update-artist-curated",
			"change-plan-amount",
		];
		let thirdArray = [...secondArray, "delete-admin"];

		let obj = {
			access1: oneArray,
			access2: secondArray,
			access3: thirdArray,
		};

		return obj[body];
	} catch (error) {
		throw error;
	}
};

